import React, { useEffect, useReducer } from 'react'
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import axios from 'axios';

function reducer(state , action)
{
    switch(action.type)
    {
      case ('fetch_data') : return {loading : false , products : [action.payload]} 

      default : return {loading : false , err : action.payload}
    }
}

export default function ProductScreen() {

  const [state , dispatch] = useReducer(reducer , {loading:true})
  const {loading , products , err} = state
  useEffect(()=>{
    try
    {
      async function fetch_d()
        {
          const data = await axios.get('')
          dispatch({type : 'fetch_data' , payload : data.result})
        }
       fetch_d() 
    }catch(err)
    {
      dispatch({type : 'fetch_err' , payload : err})
    }
  } , [])
  return (
    <div>
        <Row>
          <Col lg = '3' md = '2' sm = '1'>
              

          </Col>
          <Col lg = '3' md = '2' sm = '1'>
              
          
          </Col>
          <Col lg = '3' md = '1' sm = '1'>
              
          
          </Col>
        </Row>
    </div>
  )
}
