import React, { useContext } from 'react'
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import ListGroupItem from 'react-bootstrap/esm/ListGroupItem';
import ListGroup from 'react-bootstrap/ListGroup';
import { Store } from '../Store';
import Card from 'react-bootstrap/Card';
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';

function Cart_itmes_l(total , item)
    {
      return total + item.quantity
    }

function total_Price(total , item )   
    {
      return total + (item.quantity*item.price)
    }
export default function CartScreen() {
  const {state , dispatch} =  useContext(Store)
  const {cart} = state 
  const {cartitems} = cart

  return (
    <>
    <h1>Shoping cart</h1>
    
    <Row>
      <Col sm={1} md={8} lg={8}>
          <ListGroup>
            {cartitems.map((x)=>{
              
              return <ListGroupItem key={x} >
                <Row className='align-items-center hp '>
                  <Col className='d-flex align-items-center' sm={4} md={4} lg={4}>
                    
                      <Card style={{ width: '18rem'  , height : '100%'}}><Card.Img className='img-fluid rounded img-thumbnail' variant="top" src={x.img} /></Card>
                      &nbsp;&nbsp;
                     <Link to={`/products/${x.slug}`}>{x.name}</Link>
                  </Col>
                  <Col sm={3} md={3} lg={4}>
                      <Button className='s_icon' disabled = {x.quantity === 1}>
                          <i className='fas fa-minus-circle'></i>
                      </Button>&nbsp;&nbsp;
                      <span>{x.quantity}</span>&nbsp;&nbsp;
                      <Button disabled = {x.countInStock === x.quantity }>
                          <i className={'fas fa-plus-circle'}></i>
                      </Button>
                  </Col>
                  <Col sm={2} md={2} lg={1}>
                      <span>{x.price} $</span>
                  </Col>
                  <Col sm={2} md={2} lg={2}>
                      <Button>
                          <i className={'fas fa-trash'} ></i>
                      </Button>
                  </Col>
                </Row>
              </ListGroupItem>



            })}
              
          </ListGroup>
      </Col>
      <Col>
            <ListGroup>
              <ListGroupItem><h3>{`Subtotal (${cartitems.reduce(Cart_itmes_l , 0)} items) : ${cartitems.reduce(total_Price , 0)} dh`}</h3></ListGroupItem>
              <ListGroupItem>
              <Link to={`/`}>
               <Button variant="primary" className='m-2'>proceed to checkout</Button>
              </Link>
              </ListGroupItem>
            </ListGroup>
      </Col>
    </Row>
    </>
  )
}
