import React, { useContext, useEffect } from 'react'
import Container from 'react-bootstrap/esm/Container'
import Row from 'react-bootstrap/esm/Row';
import Col from 'react-bootstrap/esm/Col';
import { useReducer} from 'react';
import { Store } from '../Store';
import axios from 'axios';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
function reducer(state , action)
    {
        switch(action.type)
            {
                case 'ORDERS_REQ' : 
                    return {...state , loading : true}    
                    break;

                case 'ORDERS_ERR' : 
                    console.log("err inside reducer " , action.payload)
                    return {...state , err : action.payload , loading : false}    
                    break;
                default:
                    return state    
            }

    }
export default function OrderHistoryScreen() {

    const [{err , loading} , dispatch] = useReducer(reducer , {err : false , loading : true})
    const {state} = useContext(Store)
    const {userinfo} = state

    useEffect(()=>{
        dispatch({type : 'ORDERS_REQ'})

        async function fetch_orders()
            {
                try{
                    const result = await axios.get(`/api/order/all`  , {
                        headers : 
                            {
                                authorization :  `Bearer ${state.userinfo.token}`
                            }
                    })          
                }
                catch(err){
                        console.log("err inside catch" , err.message)
                        dispatch({type : 'ORDERS_ERR' , payload : err})
                }
            }

            fetch_orders()
    } , [userinfo.token])
  return (
    
    loading ? 
       <LoadingBox></LoadingBox>

    : err ? 
        <Container>
        <MessageBox variant='danger'key='primary' err={err}></MessageBox>
        </Container>
    :      
    
        <Container>
            <h1>Order History</h1>

            <Row className='mt-4'>
                <Col lg = '4'>
                    <strong>ID</strong>
                </Col>
                <Col>
                    <strong>DATE</strong>
                </Col>
                <Col>
                    <strong>TOTAL</strong>
                </Col>
                <Col>
                    <strong>DELIVRED</strong>
                </Col>
                <Col>
                    <strong>ACTIONS</strong>
                </Col>
            </Row>
        </Container>
        
)
}
