import React, { useEffect, useReducer } from 'react'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import axios from 'axios'
import loadingBox from '../components/loadingBox';
import MessageBox from '../components/MessageBox';

function reducer(state , action)
{
    switch(action.type)
    {
        case 'loading' : return {...state , loading:true}

        case 'error' : return {...state , loading:false , err:action.payload}

        default : return {loading : false , err : false , products : action.payload}
    }
}
export default function HomeScreen() {
    const {state , dispatch}    =   useReducer(reducer , {loading : true , products : [] , err:''})
    const {loading , err , products} = state

    useEffect(()=>{
        dispatch({type : 'loading'})
            
        async function fetch_product(){
            try
            {
                const data = await axios.get('/products')
                const {products} = data
                dispatch({type:'products' , payload : products})
            }catch(err)
            {
                dispatch({type:'error' , payload : err})
            }
            
            
        }
        fetch_product()
    } , [dispatch])
  return (
    <div><h1>Nos produits</h1>
    {loading ? 
            <loadingBox/>
        : err ? 
            <MessageBox key='primary'>{err}</MessageBox>
        :        
    <div className='products'>
      {
        products.map((x)=>(
          <div key={x.slug} className='product'>
            <Card style={{ width: '18rem'  , height : '100%'}}>
            <Card.Img variant="top" src={x.image} />
              <Card.Body>
                    <Card.Title>{x.name}</Card.Title>
                    <Card.Text>
                       {x.description}
                    </Card.Text>
              <Button variant="primary">add to card</Button>
              </Card.Body>
            </Card>
          </div>
        ))
      }
      
    </div>}</div>
    
  )
}
