import React, { useContext } from 'react'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Raiting from './Raiting';
import { Link } from 'react-router-dom';
import { Store } from '../Store';

export default function Product({x}) {
  const {dispatch} = useContext(Store)
  function handleClick()
    {
      console.log('it s handeled' )
      dispatch({type:'add_to_cart' , payload : {...x , quantity : 1}})
    }
  return (
    <div key={x.slug} className='product'>
            <Card style={{ width: '18rem'  , height : '100%'}}>
            <Link to={`product/${x.slug}`}><Card.Img className='mb-auto' variant="top" src={x.image} /></Link>
              <Card.Body className='d-flex  flex-column ' style={{padding: '2px'}}>
                    <Link to={`product/${x.slug}`} className='mb-1'><Card.Title className='mb-auto'>{x.name}</Card.Title></Link>
                    <Card.Text className='mb-auto'>
                       <Raiting n_reviews={x.numReviews} raiting={x.rating}></Raiting>
                    </Card.Text>
                    <Card.Text className='mb-auto'>
                       {x.description}
                    </Card.Text>
                    <Card.Text  className='mb-auto'>
                       <strong>{x.price } dh</strong>
                    </Card.Text>
              <Button variant="primary" className='m-2' onClick={handleClick}>add to card</Button>
              </Card.Body>
            </Card>
          </div>
  )
}
