import React, {createContext, useReducer } from 'react'

function reducer(state , action)
{
  let carditems = []
    switch(action.type)
     {
        case 'add_to_cart':

          const exist_item = state.card.carditems.find((x)=>(x.id === action.payload.id))
          console.log(exist_item)
          if(exist_item )
          {
           carditems = carditems.map((x)=>{if(x.id === exist_item.id) {return exist_item } else{ return x}})
           return {...state , card : {...state.card , carditems}}
          }
          else
          {
            return {...state , card : {...state.card , carditems : [...state.card.carditems , action.payload]}
          } 
          

        
       
     }
     default : return state
}
}

export const Store = createContext({})

const initialState = {
    card : {
        carditems : [],
    }
}
export  function StoreProvider({children}) {

    const [state , dispatch] = useReducer(reducer , initialState)
    const value = {state , dispatch}
  return (
    
    <Store.Provider value={value}>{children}</Store.Provider>
  )
}
