import React from 'react'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Raiting from './Raiting';
import { Link } from 'react-router-dom';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

export default function Product({x}) {
  return (
    
    <div key={x.slug} className='product'>
      
            <Card style={{ width: '18rem'  , height : '100%'}}>
            <Link to={`product/${x.slug}`}><Card.Img variant="top" classNamesrc={x.image} /></Link>
              <Card.Body className='d-flex  flex-column'>
                    <Link to={`product/${x.slug}`} className='mb-1'><Card.Title>{x.name}</Card.Title></Link>
                    <Card.Text>
                       <Raiting n_reviews={x.numReviews} raiting={x.rating}></Raiting>
                    </Card.Text>
                    <Card.Text>
                       {x.description}
                    </Card.Text>
                    <Card.Text className='mt-auto'>
                       <strong>{x.price } dh</strong>
                    </Card.Text>
              <Button variant="primary" /*className='mt-auto'*/>add to card</Button>
              </Card.Body>
            </Card>
            
          </div>
             
  )
}
