import React, { useEffect, useReducer } from 'react'
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import axios from 'axios';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import ListGroup from 'react-bootstrap/ListGroup';
import Raiting from '../components/Raiting';
import Card from 'react-bootstrap/Card';


function reducer(state , action)
{
    switch(action.type)
    {
      case ('fetch_data') : return {loading : false , products : [action.payload]} 

      default : return {loading : false , err : action.payload}
    }
}

export default function ProductScreen() {

  const [state , dispatch] = useReducer(reducer , {loading:true})
  const {loading , product , err} = state

  useEffect(()=>{
    try
    {
      async function fetch_d()
        {
          const data = await axios.get('')
          dispatch({type : 'fetch_data' , payload : data.result})
        }
       fetch_d() 
    }catch(err)
    {
      dispatch({type : 'fetch_err' , payload : err})
    }
  } , [])
  return (
    loading ? <LoadingBox/>
    :err ? <MessageBox variant='danger'key='primary' err={err}></MessageBox>
    :
    <div>
        <Row>
          <Col lg = '3' md = '2' sm = '1'>
              <img src = {product.image} alt='main_product'/>

          </Col>
          <Col lg = '3' md = '2' sm = '1'>
              <ListGroup>

                  <h1>{product.name}</h1>
                  <ListGroup.Item ><Raiting n_reviews={product.numReviews} raiting={product.rating}/></ListGroup.Item>
                  <ListGroup.Item>Price : {product.price}$</ListGroup.Item>
                  <ListGroup.Item className='products'>
                    product.sub_prod_img.map((x)=>{
                        <Card>
                          <Card.Img variant="top" src = {x} />
                        </Card>
                    })
                  </ListGroup.Item>
                  <ListGroup.Item>Porta ac consectetur ac</ListGroup.Item>
              
              </ListGroup>
          
          </Col>
          <Col lg = '3' md = '1' sm = '1'>
              
          
          </Col>
        </Row>
    </div>
  )
}
