import React, {createContext, useReducer } from 'react'

function reducer(state , action)
{
  let cartitems = state.card.cartitems
    switch(action.type)
     {
        case 'add_to_cart':
           
          const exist_item = state.cart.cartitems.find((x)=>(x.id === action.payload.id))
          
          if(exist_item )
          {
           cartitems = cartitems.map((x)=>{if(x.id === exist_item.id) {return action.payload } else{ return x}})
           
           return {...state , cart : {...state.cart , cartitems}}
          }
          
          else
          {
            return {...state , cart : {...state.cart , cartitems : [...state.cart.cartitems , action.payload]}
          } 
          

        
       
     }
     default : return state
}
}

export const Store = createContext({})

const initialState = {
    cart : {
        cartitems : [],
    }
}
export  function StoreProvider({children}) {

    const [state , dispatch] = useReducer(reducer , initialState)
    const value = {state , dispatch}
  return (
    
    <Store.Provider value={value}>{children}</Store.Provider>
  )
}
