import React, { useEffect, useState } from 'react'
import Form  from 'react-bootstrap/Form';
import Container from 'react-bootstrap/esm/Container';
import CheckoutSteps from '../components/CheckoutSteps';
import Button from 'react-bootstrap/esm/Button';
import { Store } from '../Store';
import { useNavigate } from 'react-router-dom';

export default function PaymentScreen() {
    const [setPaymentMethod , PaymentMethod] = useState('')
    const {state , dispatch} = useContext(Store)
    const {cart} = state
    const shipping = cart.shipping
    const Navigate = useNavigate()
   function handleSubmit(e)
    {
        e.preventDefault()


    }
    useEffect(()=>{if(!shipping)
                    Navigate('/shipping')} , [shipping , Navigate])
  return (
    <>
    <CheckoutSteps  step1 step2 step3></CheckoutSteps>
    <Container className='mt-4'style={{
         maxWidth:'fit-content',
         marginInline:'auto'
    }}>
    <h1 className='mb-3'>Payment Method</h1>
    <Form  style={{textAlign:'left', maxWidth:'45rem'}} onSubmit={(e)=>{handleSubmit(e)}}>

        <Form.Check
            label="PayPal"
            name="group1"
            type='radio'
            id={`inline-1`}
            onChange={(e)=>{setPaymentMethod(e.target.value)}}
            />
          
          <Form.Check
            label="Stripe"
            name="group1"
            type='radio'
            id={`inline-2`}
            className='mt-3'
            onChange={(e)=>{setPaymentMethod()}}
            />
           <Button type='submit' className='mt-3 wb'>Continue</Button>
     </Form>
     </Container>
     </>
  )
}
