import React, { useEffect, useReducer, useState } from 'react'
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import axios from 'axios';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import ListGroup from 'react-bootstrap/ListGroup';
import Raiting from '../components/Raiting';
import Card from 'react-bootstrap/Card';
import Badge from 'react-bootstrap/Badge';
import { useLocation } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Table from 'react-bootstrap/Table';

function reducer(state , action)
{
    switch(action.type)
    {
      case ('fetch_data') : return {loading : false , product : action.payload} 

      default : return {loading : false , err : action.payload}
    }
}

export default function ProductScreen() {
  const [selected , setSelected] = useState('')
  let location = useLocation()
  const [state , dispatch] = useReducer(reducer , {loading:true})
  const {loading , product , err} = state

  useEffect(()=>{
    try
    {
      async function fetch_d()
        {
          

          const result = await axios.get(`${location.pathname}`)
          
          dispatch({type : 'fetch_data' , payload : result.data})
        }
       fetch_d() 
    }catch(err)
    {
      dispatch({type : 'fetch_err' , payload : err})
    }
  } , [dispatch])
  return (
    loading ? <LoadingBox/>
    :err ? <MessageBox variant='danger'key='primary' err={err}></MessageBox>
    :
    <div>
        <Row>
          <Col lg = '5' md = '5' sm = 'auto'>
              <img src = {selected ? selected : product.image} alt='main_product' className='w-100 h-100 rounded border border-dark '/>

          </Col>
          <Col lg = '4' md = '4' sm = 'auto'>
              <ListGroup>

                  <h1>{product.name}</h1>
                  <ListGroup.Item ><Raiting n_reviews={product.numReviews} raiting={product.rating}/></ListGroup.Item>
                  <ListGroup.Item>Price : {product.price}$</ListGroup.Item>
                  <ListGroup.Item className='products'>
                    <Row>
                    {
                     
                      product.sub_prod_img.map((x)=>
                      
                        ( <Col key={x}>
                        <Button  variant="outline-light" onClick={()=>{setSelected(x)}}>
                        <Card >
                          <Card.Img variant="top" src = {x} />
                        </Card>
                        </Button>
                        </Col>)
                     )
                     
                    }
                    </Row>
                  </ListGroup.Item>
                  <ListGroup.Item><span>Description : </span> <p>{product.description}</p></ListGroup.Item>
              
              </ListGroup>
          
          </Col>
          <Col lg = '3' md = '3' sm = '12'>
            
              <Card>
              <ListGroup>
                  <ListGroup.Item>
                    <Row>
                      <Col>
                        <p>Price : </p>
                      </Col>
                      <Col>
                        <p>{product.price}$</p>
                      </Col>
                    </Row>
                    </ListGroup.Item>
                  
                  <ListGroup.Item>
                    <Row>
                      <Col>
                        <p>Status: </p>
                      </Col>
                      <Col>
                        <p>{product.countInStock ? <Badge bg="success">availible</Badge>
                            :<Badge bg="danger">Unavailible</Badge>}</p>
                      </Col>
                    </Row>
                    </ListGroup.Item>
                    </ListGroup>
                  
              </Card>
             <Row className='mt-5'>
                  <Col>
                  <Table striped bordered hover>
      
      <tbody>
        <tr>
          <td>1</td>
          <td>Mark</td>
          
        </tr>
        <tr>
          <td>2</td>
          <td>Jacob</td>
          
        </tr>
        <tr>
          <td>3</td>
          <td colSpan={2}>Larry the Bird</td>
         
        </tr>
      </tbody>
    </Table>
                  </Col>
             </Row>
             
          </Col>
        </Row>
    </div>
  )
}
