          {
          const exist_item = state.cart.cartitems.find((x)=>(x.id === action.payload.id))
          
          if(exist_item )
          {
           cartitems = cartitems.map((x)=>{if(x.id === exist_item.id) {return action.payload } else{ return x}})
           
           return {...state , cart : {...state.cart , cartitems}}
          }
          
          else
          {
            return {...state , cart : {...state.cart , cartitems : [...state.cart.cartitems , action.payload]}
          } 
          

        
         
