import React, { useEffect, useReducer } from 'react'
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import axios from 'axios';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import ListGroup from 'react-bootstrap/ListGroup';
import Raiting from '../components/Raiting';
import Card from 'react-bootstrap/Card';
import Badge from 'react-bootstrap/Badge';


function reducer(state , action)
{
    switch(action.type)
    {
      case ('fetch_data') : return {loading : false , products : [action.payload]} 

      default : return {loading : false , err : action.payload}
    }
}

export default function ProductScreen() {

  const [state , dispatch] = useReducer(reducer , {loading:true})
  const {loading , product , err} = state

  useEffect(()=>{
    try
    {
      async function fetch_d()
        {
          const data = await axios.get('/product/:slug')
          dispatch({type : 'fetch_data' , payload : data.result})
        }
       fetch_d() 
    }catch(err)
    {
      dispatch({type : 'fetch_err' , payload : err})
    }
  } , [])
  return (
    loading ? <LoadingBox/>
    :err ? <MessageBox variant='danger'key='primary' err={err}></MessageBox>
    :
    <div>
        <Row>
          <Col lg = '3' md = '2' sm = '1'>
              <img src = {product.image} alt='main_product'/>

          </Col>
          <Col lg = '3' md = '2' sm = '1'>
              <ListGroup>

                  <h1>{product.name}</h1>
                  <ListGroup.Item ><Raiting n_reviews={product.numReviews} raiting={product.rating}/></ListGroup.Item>
                  <ListGroup.Item>Price : {product.price}$</ListGroup.Item>
                  <ListGroup.Item className='products'>
                    {product.sub_prod_img.map((x)=>
                      
                        (<Card key={x}>
                          <Card.Img variant="top" src = {x} />
                        </Card>)
                     )
                    }
                  </ListGroup.Item>
                  <ListGroup.Item><span>Description : </span> <p>{product.description}</p></ListGroup.Item>
              
              </ListGroup>
          
          </Col>
          <Col lg = '3' md = '1' sm = '1'>
              <Card>
                  <ListGroup.Item>
                    <Row>
                      <Col>
                        <p>Price : </p>
                      </Col>
                      <Col>
                        <p>{product.price}$</p>
                      </Col>
                    </Row>
                    </ListGroup.Item>
                  <ListGroup.Item>
                  <ListGroup.Item>
                    <Row>
                      <Col>
                        <p>Status : </p>
                      </Col>
                      <Col>
                        <p>{product.countInStock ? <Badge bg="success">availible</Badge>
                            :<Badge bg="danger">Unavailible</Badge>}</p>
                      </Col>
                    </Row>
                    </ListGroup.Item>
                  </ListGroup.Item>
                  
              </Card>
          
          </Col>
        </Row>
    </div>
  )
}
