import logo from './logo.svg';
import Navbar from 'react-bootstrap/Navbar'
import Nav from 'react-bootstrap/Nav'
import Container from 'react-bootstrap/Container'
import './App.css';
import { Link, Route, Routes } from 'react-router-dom';
import HomeScreen from './Screens/HomeScreen';
import ProductScreen from './Screens/ProductScreen';
import { useContext } from 'react';
import {Store} from './Store'
import Badge from 'react-bootstrap/esm/Badge';

function App() {

  const {state} = useContext(Store)



  //const {card}  = state


  return (
    <div className="App" >
      <header>
         <Navbar className='bg-green' data-bs-theme="dark">
            <Container >
              <Link  className='nav-link' to={'/'}>
                <Navbar.Brand href='#' style={{fontSize : '23px' , color:'white'}}>BioNourri</Navbar.Brand>
              </Link>

               <Nav>
                  <Link className=' nav-link ' to='/cart'>
                  <span style={{fontSize : '23px' , color:'white'}}>Cart</span>
                      {

                        state.card.carditems.lenght > 0 ? 
                        <Badge bg="secondary">9</Badge>
                        :<Badge bg="secondary">5</Badge>
                      }
                  </Link>
                </Nav> 
            </Container>
         </Navbar>

      </header>

      <main>
          <Routes>
            <Route path="/" element={<HomeScreen/> } /> 
             <Route path='/product/:slug' element = {<ProductScreen/>}></Route>
          </Routes>
      </main>
    </div>
  );
}

export default App;
